import React, { useState, useEffect, useRef } from "react";
import "../chat.css";

import { ChatBubble } from "./chatbubble";
import { TypingAnimation } from "./typinganimation";
import { TypingBubble } from "./typingbubble";

export const Chat = (props) => {
  const [chatlog, setChatlog] = useState(props.chatData);
  const [feedback, setFeedback] = useState("");
  const [triggerResponse, setTriggerResponse] = useState(false);
  const [isDisable, setIsDisable] = useState(false);
  const [isUpdateIdea, setIsUpdateIdea] = useState(props.isUpdateIdea);
  const [isQuestionUpdated, setIsQuestionUpdated] = useState(false);
  const [isReset, setIsReset] = useState(false);
  const divRef = useRef(null);
  const textareaRef = useRef(null);

  const giveFeedback = () => {
    if (feedback !== "") {
      setChatlog([...chatlog, { speaker: "instructor", content: feedback }]);
      setTriggerResponse(true);
    }
  };

  const handleFeedbackChange = (event) => {
    setFeedback(event.target.value);
    adjustHeight();
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      giveFeedback();
    }
  };

  useEffect(() => {
    // console.log(triggerResponse);
    async function fetchData() {
      if (triggerResponse) {
        await props.getResponse(feedback);
        setTriggerResponse(false); // Reset trigger
        setFeedback("");
        setIsDisable(true);
      }
    }
    fetchData();
  }, [triggerResponse]);

  useEffect(() => {
    setChatlog(props.chatData);
    setTimeout(() => {
      setIsQuestionUpdated(false);
    }, 500);
    async function askQuestion() {
      await props.getQuestion();
      setIsQuestionUpdated(true);
      setIsReset(true);
      setTimeout(() => setIsReset(false), 100);
    }
    if (isDisable) {
      if (props.questionChecker) {
        askQuestion();
      } else if (!isQuestionUpdated) {
        setIsDisable(false);
      }
    }
  }, [props.chatData]);

  useEffect(() => {
    if (divRef.current) {
      divRef.current.scrollTo({
        top: divRef.current.scrollHeight,
        behavior: "smooth",
      });
    }
  }, [chatlog, isDisable]);

  const adjustHeight = () => {
    if (textareaRef.current) {
      // Reset height to recalculate
      textareaRef.current.style.height = "14px";
      // Set height based on the scroll height which reflects the content height
      const maxHeight = 112; // Example max height in pixels for about 5 lines
      textareaRef.current.style.height = `${Math.min(
        textareaRef.current.scrollHeight,
        maxHeight
      )}px`;
    }
  };

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
      adjustHeight(); // Adjust height on initial render to ensure it fits the content
    }
  }, [feedback]);

  useEffect(() => {
    setIsUpdateIdea(props.isUpdateIdea);
  }, [props.isUpdateIdea]);

  return (
    <>
      <div className="chatUI">
        <div className="chatWindow" ref={divRef}>
          <div className="chatContainer">
            {chatlog.map((chat, index) => (
              <ChatBubble key={index} speaker={chat.speaker} content={chat.content} mode={1} />
            ))}
            {isDisable ? (
              <TypingBubble
                content={<TypingAnimation interval={800} isDisable={isDisable} reset={isReset} />}
              />
            ) : null}
          </div>
        </div>
        <div
          className={isDisable || isUpdateIdea ? "disabled bottombar" : "bottombar"}
          style={{ height: "auto" }}
        >
          <textarea
            ref={textareaRef}
            id="bottomtextarea"
            style={{ height: "auto" }}
            value={feedback}
            onKeyDown={handleKeyDown}
            onChange={handleFeedbackChange}
            disabled={isDisable || isUpdateIdea}
            placeholder="피드백을 작성하세요..."
          />
          <img
            className="chatBtn"
            src="images/chatBtn.png"
            alt="chatBtn"
            onClick={giveFeedback}
            disabled={isDisable}
          />
        </div>
      </div>
    </>
  );
};
