import React, {useState, useEffect} from 'react';
import './board.css'

export const StickyNote = ({ initialPos, content, id, onMove, parentRef}) => {
    const [isDragging, setIsDragging] = useState(false);
    const [position, setPosition] = useState(initialPos);
    const [relPosition, setRelPosition] = useState({ x: 0, y: 0 }); // Relative position of the cursor

    const onMouseDown = (e) => {
        if (e.button !== 0) return; // Only left mouse click
        const elem = e.target.getBoundingClientRect();
        setRelPosition({
            x: e.clientX - elem.left, // Calculate relative position
            y: e.clientY - elem.top,
        });
        setIsDragging(true);
        e.stopPropagation();
        e.preventDefault();
    };

    const onMouseUp = (e) => {
        setIsDragging(false);
        e.stopPropagation();
        e.preventDefault();
        onMove(id, position); // Update the position in the parent component
    };

    const onMouseMove = (e) => {
        if (!isDragging) return;
        if (parentRef && parentRef.current) {
                const parentRect = parentRef.current.getBoundingClientRect();
            setPosition({
                x: e.clientX - parentRect.left - relPosition.x,
                y: e.clientY - parentRect.top - relPosition.y
            });
        }
        e.stopPropagation();
        e.preventDefault();
    };

    useEffect(() => {
        document.addEventListener("mouseup", onMouseUp);
        document.addEventListener("mousemove", onMouseMove);

        return () => {
            document.removeEventListener("mouseup", onMouseUp);
            document.removeEventListener("mousemove", onMouseMove);
        };
    });

    return(
        <>
            <div className='stickynote' 
                onMouseDown={onMouseDown}
                onMouseMove={onMouseMove}
                onMouseUp={onMouseUp}
                style={{ 
                    left: position.x, 
                    top: position.y, 
                    position: 'absolute' 
                }}>
                {content} X:{position.x}, Y:{position.y} <br/>
                isDragging: {isDragging? "True" : "False"}
            </div>
        </>
    )
}
