import '../chat.css'

import { ChatBubble } from './chatbubble';


export const Chat = (props) => {

    const [chatlog, setChatlog] = useState(props.chatData);

    const getResponse = () => {
        axios({
            method: "POST",
            url:"/response",
            headers: {
                Authorization: 'Bearer ' + props.token
            },
            data: {notesData: currentNotesData}
            })
            .then((response) => {
                const res =response.data
                console.log(res.response)
                console.log(typeof(res.response))
            }).catch((error) => {
            if (error.response) {
                console.log(error.response)
                console.log(error.response.status)
                console.log(error.response.headers)
            }
        })
    }

    return(
        <>
            <div className='chatUI'>
                <div className='chatWindow'>
                    <div className='chatContainer'>
                        {chatlog.map((chat, index) => (
                            <ChatBubble
                                key = {index}
                                speaker = {chat.speaker}
                                content = {chat.content}
                            />
                        ))}
                    </div>
                </div>
                <div className='bottombar'>
                    <input placeholder='Message IDMentee...'/>
                    <img className='chatBtn' src='images/chatBtn.png' alt='chatBtn'/>
                </div>
            </div>
        </>
    )
}
