import React, { useState } from 'react';
import './board.css'

export const Board = (props) => {

    const [notesData, setNotesData] = useState([{"num": 0, "contents": "hi", "x": 0, "y": 0}, {"num": 1, "contents": "hi1", "x": 200, "y": 200}]);

    return(
        <>
            <div className='board'>
                <div className='boardUI'>
                    <div className='noteContainer'>
                        {notesData.map(noteData => (
                            <div className='note'>
                                {noteData.contents}
                            </div>
                        ))}
                    </div>
                </div>
                <div className='bottombar'>
                    <button>add note</button>
                </div>
            </div>
        </>
    )
}
