import React, {useState, useEffect} from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

import './topbar.css'

export const Topbar = (props) => {

    const navigate = useNavigate();
    const location = useLocation();
    const [isFeedback, setIsFeedback] = useState(false);

    function logout() {
        axios({
          method: "POST",
          url:"/logout",
        })
        .then((response) => {
           props.removeToken()
        }).catch((error) => {
          if (error.response) {
            console.log(error.response)
            console.log(error.response.status)
            console.log(error.response.headers)
            }
    })}

    function finish() {
        navigate('/');
    }

    useEffect(() => {
        try {
            isFeedback = location.state?.feeback;
        }
        catch (error) {
        }
    }, []);

    return(
        <>
            <div className='topbar'>
                <img src='logo192.png' alt='logo'/>
                <input className="burger-check" type="checkbox" id="burger-check" /><label className="burger-icon" htmlFor="burger-check"><span className="burger-sticks"></span></label>
                <div className='menu'>
                    {isFeedback ? 1:0}
                    {isFeedback ? 
                        <button className='hamburger-bar' onClick={finish}>Finish the Round</button> 
                        :
                        <button className='hamburger-bar' onClick={finish}>Finish the Round</button> 
                    }                         
                    <button className='hamburger-bar' onClick={logout}>Logout</button>
                </div>
            </div>
        </>
    )
}
