import React, {useState, useEffect} from 'react';
import './board.css'

export const StickyNote = ({ initialPos, content, id, onMove }) => {
    const [isDragging, setIsDragging] = useState(false);
    const [position, setPosition] = useState(initialPos);

    const onMouseDown = (e) => {
        if (e.button !== 0) return; // Only left mouse click
        setIsDragging(true);
        e.stopPropagation();
        e.preventDefault();
    };

    const onMouseMove = (e) => {
        if (!isDragging) return;
        setPosition({
            x: e.clientX,
            y: e.clientY
        });
        e.stopPropagation();
        e.preventDefault();
    };

    const onMouseUp = (e) => {
        setIsDragging(false);
        e.stopPropagation();
        e.preventDefault();
        onMove(id, position); // Update the position in the parent component
    };

    useEffect(() => {
        document.addEventListener("mouseup", onMouseUp);
        document.addEventListener("mousemove", onMouseMove);

        return () => {
            document.removeEventListener("mouseup", onMouseUp);
            document.removeEventListener("mousemove", onMouseMove);
        };
    });

    return(
        <>
            <div className='stickynote' 
                onMouseDown={onMouseDown}
                style={{ 
                    left: position.x + 'px', 
                    top: position.y + 'px', 
                    position: 'absolute' 
                }}>
                {content} X:{position.x}, Y:{position.y}
            </div>
        </>
    )
}
