import React, { useState, useRef } from 'react';
import axios from 'axios';
import './board.css';

import { StickyNote } from './stickynote';

export const Board = (props) => {

    const [currentNotesData, setCurrentNotesData] = useState(props.notesData);
    const parentRef = useRef(null);

    const addNote = () => {

        axios({
            method: "GET",
            url:"/addNote",
            headers: {
                Authorization: 'Bearer ' + props.token
            }
            })
            .then((response) => {
            const res =response.data
            res.access_token && props.setToken(res.access_token)
            setCurrentNotesData(
                res.notesData
            )
            }).catch((error) => {
            if (error.response) {
                console.log(error.response)
                console.log(error.response.status)
                console.log(error.response.headers)
            }
        })
    }


    const moveNote = (id, newPos) => {
        setCurrentNotesData(currentNotesData.map(noteData => {
            return noteData.id === id ? { ...noteData, position: newPos } : noteData;
        }));
    };

    const deleteNote = (id) => {
        setCurrentNotesData(currentNotesData => currentNotesData.filter(noteData => noteData.id !== id));
    }


    return(
        <>
            <div className='board'>
                <div className='boardUI'>
                    <div className='noteContainer' ref={parentRef}>
                        {currentNotesData.map((noteData, index) => (
                            <StickyNote
                                key = {index}
                                initialPos={noteData.position}
                                initalContent={noteData.content}
                                id={index}
                                moveNote={moveNote}
                                deleteNote ={() => deleteNote(index)}
                                parentRef={parentRef}
                            />
                        ))}
                    </div>
                </div>
                <div className='bottombar'>
                    <button onClick={addNote}>add note</button>
                    <button onClick={() => deleteNote(1)}>delete note</button>
                    <button>save</button>
                </div>
            </div>
        </>
    )
}
