export const RadarData = {
  labels: ["독창성", "연관성", "난이도", "구체성", "타당성", "실행가능성"],
  datasets: [
    {
      backgroundColor: "rgba(44, 84, 242, .5)",
      // borderColor: "#2C54F2",
      // pointBackgroundColor: "#2C54F2",
      // pointBorderColor: "#fff",
      pointHoverBackgroundColor: "#fff",
      // pointHoverBorderColor: "#2C54F2",
      data: [7, 6, 5.1, 2.1, 6, 5]
    }
  ]
};

export const RadarOptions = {
  plugins: {
    legend: {
        display: false
    }
  },
  scales: {
    r: {  // This assumes you are using Chart.js version 3 or later
      ticks: {
        display: false,  // This disables the tick labels
      },
      angleLines: {
        color: "rgba(255, 255, 255, .3)",
        lineWidth: 1
      },
      gridLines: {
        color: "rgba(255, 255, 255, .3)",
        circular: true
      }
    }
  }
};
