import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export class BarChart extends Component {
  constructor(props) {
    super(props);
    this.chartRef = React.createRef();
  }

  render() {
    const barData = {
      labels: ["독창성", "연관성", "난이도", "구체성", "타당성", "실행가능성"],
      datasets: [
        {
          backgroundColor: "rgba(44, 84, 242, .7)",
          data: this.props.evalPoint,
          borderRadius: 5, // Example of setting borderRadius for bars
        }
      ]
    };

    const barOptions = {
      responsive: true,
      plugins: {
        legend: {
          display: false,
        },
        title: {
          display: true,
          text: 'Chart.js Bar Chart',
        },
      },
    }

    return (
      <Bar ref={this.chartRef} height={200} data={barData} options={barOptions} />
    );
  }
}
