import React, { useState, useEffect } from 'react';

export const TypingAnimation = ({ text, interval = 500, isDisable ,reset }) => {
    const [displayText, setDisplayText] = useState('음');
    const [index, setIndex] = useState(0);

    useEffect(() => {
        let typingInterval;

        if (isDisable) {
            typingInterval = setInterval(() => {
                setIndex(prevIndex => {
                    if (prevIndex < text.length) {
                        setDisplayText(text.substring(0, prevIndex + 1));
                        return prevIndex + 1;
                    } else {
                        // Reset to start the animation again
                        setDisplayText('음');
                        return 0;
                    }
                });
            }, interval);
        }

        return () => {
            clearInterval(typingInterval);
            setDisplayText('음'); // Reset the display text when stopping
            setIndex(0);        // Reset the index when stopping
        };
    }, [isDisable, text, interval]);

    useEffect(() => {
        if (reset) {
            setDisplayText('음');
            setIndex(0);
        }
    }, [reset]);

    return <div>{displayText}</div>;
};
