import React, { useState, useRef } from 'react';
import axios from 'axios';
import './ideaContainer.css'

export const IdeaContainer = (props) => {

    const[ideas, setIdeas] = useState([{"title":"1", "problem":"no~~", "idea":"yasfdkjhas;fljsaghf;jlash"}, {"title":"2", "problem":"no~~", "idea":"yasfdkjhas;fljsaghf;jlash"}]);
    const[currentIdea, setCurrentIdea] = useState(0);

    const ChangeIdea = (i) => {
        if(currentIdea != i) {
            setCurrentIdea(i);
        }
    }

    return(
        <>
            <div className='ideaContainerUI'>
                <div className='navbar'>
                    {ideas.map((idea, index)=>
                        <button className='ideaTag' key={index}>
                            idea {index + 1}
                        </button>
                    )}
                </div>
                <div className='ideaContainer'>
                    {ideas.map((idea, index) => (index === currentIdea) ?
                        <div className='ideaBox' key={index}>
                            <div className='ideatitle'>{idea.title}</div>
                            <div className='problem'>{idea.problem}</div>
                            <div className='idea'>{idea.idea}</div>
                        </div>
                        :
                        null
                    )}
                </div>
            </div>
        </>
    )
}
