import React, { useState, useRef } from 'react';
import axios from 'axios';
import '../chat.css'

export const ChatBubble = ({speaker, content}) => {
    return(
        <>
            <div className='chatbubble'>
                {speaker == "student" ? 
                    <div className='chatBox'>
                        <img src='logo192.png' alt='logo'/>
                        <div>
                            {content}
                        </div>
                    </div>
                    :
                    <div>
                    </div>
                }
            </div>
        </>
    )
}
