import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

export class BarChart extends Component {
  constructor(props) {
    super(props);
    this.chartRef = React.createRef();
  }

  render() {
    const barData = {
      labels: ["독창성", "연관성", "난이도", "구체성", "타당성", "실행가능성"],
      datasets: [
        {
          backgroundColor: "rgba(44, 84, 242, .7)",
          data: this.props.evalPoint,
          borderRadius: 5, // Example of setting borderRadius for bars
        }
      ]
    };

    const barOptions = {
      legend: {
        display: false, // label 숨기기
      },
      scales: {
          yAxes: [{
              ticks: { 
                  min: 0, // 스케일에 대한 최솟갓 설정, 0 부터 시작
                  stepSize: 1, // 스케일에 대한 사용자 고정 정의 값
              }
          }]
      },
      maintainAspectRatio: false // false로 설정 시 사용자 정의 크기에 따라 그래프 크기가 결정됨.
    }

    return (
      <Bar ref={this.chartRef} data={barData} options={barOptions} />
    );
  }
}
