import React, { useState, useEffect } from 'react';

const TypingAnimation = ({ text, interval = 500, isAble }) => {
    const [displayText, setDisplayText] = useState('');
    const [index, setIndex] = useState(0);

    useEffect(() => {
        let typingInterval;

        if (isAble) {
            typingInterval = setInterval(() => {
                setIndex(prevIndex => {
                    if (prevIndex < text.length) {
                        setDisplayText(text.substring(0, prevIndex + 1));
                        return prevIndex + 1;
                    } else {
                        // Reset to start the animation again
                        setDisplayText('');
                        return 0;
                    }
                });
            }, interval);
        }

        return () => {
            clearInterval(typingInterval);
            setDisplayText(''); // Reset the display text when stopping
            setIndex(0);        // Reset the index when stopping
        };
    }, [isAble, text, interval]);

    return <div>{displayText}</div>;
};

export default TypingAnimation;
